consulta2_string = "SELECT NombreDePila AS Nombre_Estudiante, rut AS rut_Estudiante, i.numSerie AS Num_Serie_Intrumento FROM Estudiante e INNER JOIN prestamo_eventual p ON e.rut = p.rutest INNER JOIN Instrumento i ON p.NumSerieInst = i.NumSerie"
consulta4_string = "SELECT e.RUT AS RUT_Estudiante, i.NumSerie AS Num_Serie_Instrumento, s.EstadoSolicitud, COUNT(s.RutEst) AS Cant_Veces_Prestado FROM Estudiante e INNER JOIN Solicita s ON e.RUT = s.RutEst INNER JOIN Instrumento i ON i.NumSerie = s.NumSerieInst WHERE e.RUT = '{selected_item}' GROUP BY e.RUT, s.RutEst, i.NumSerie, s.EstadoSolicitud"
